sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	//gs.addErrorMessage('test error : '+current.parent);
	var objParent = new SetParentPriorityIfChildMorethan2();
	var noOfParentInc = objParent.fCheckIncidentCountForParent(current.parent);
	var noOfParentIncInt = parseInt(noOfParentInc);
	gs.addInfoMessage(noOfParentIncInt);
    if ( noOfParentIncInt >= 2){
		var rec = new GlideRecord('x_58872_needit_needit');
		rec.addQuery('number',current.parent.number);
		rec.query();
		if(rec.next()){
			if(rec.priority !=1)
				{
				 rec.setValue('urgency',1);
				rec.setValue('impact',1);
				rec.setValue('priority',1);
				rec.update();
				gs.addInfoMessage('parent record updated'+ rec.urgency);	
				}
			else{
				gs.addInfoMessage('Parent is already set to Critical');
			}

		}
		else{
			gs.addInfoMessage('there are no parent records');
		}

	}
})(current, previous);
