sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	if (isLoading && !g_form.isNewRecord()|| newValue === ''|| newValue==oldValue) {
      return;
   }
	var getEmail = new GlideAjax('GetEmailAddress');
	getEmail.addParam('sysparm_name','getEmail');
	getEmail.addParam('sysparm_userID',g_form.getValue('u_requested_for'));
	getEmail.getXML(populateEmailAddress);
	function populateEmailAddress(response){
		var varEmail = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('email');
		g_form.setValue('email',varEmail);

	}


   //Type appropriate comment here, and begin script below

}
