sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	function getPendingIncCount(fvAssignmentGroup) {
        var incRec = new GlideAggregate('x_58872_needit_needit');
        incRec.addQuery('active', 'true');
        incRec.addQuery('assignment_group', fvAssignmentGroup);
        incRec.addAggregate('COUNT');
        incRec.query();
        if (incRec.next()) {
            var aggCount = incRec.getAggregate('COUNT');
            return aggCount;
        } else
            return ('No pending Incidents for ' + fvAssignmentGroup.name);
    }

    if (current.assignment_group == '') {
        if (current.u_request_type == 'hr') {
            current.assignment_group = '254878891b2d1010bca14196bc4bcbf9';
			current.update();
            gs.addInfoMessage('Total HR Incidents : ' + getPendingIncCount('254878891b2d1010bca14196bc4bcbf9'));

        } else if (current.u_request_type == 'legal') {
            current.assignment_group = 'f1f8d0a81b5e1010bca14196bc4bcb61';
			current.update();
            gs.addInfoMessage('Total Legal Incidents : ' + getPendingIncCount('f1f8d0a81b5e1010bca14196bc4bcb61'));
        } else if (current.u_request_type == 'facilities') {
            current.assignment_group = 'cd1898681b5e1010bca14196bc4bcb86';
			current.update();
            gs.addInfoMessage('Total Facilities Incidents : ' + getPendingIncCount('cd1898681b5e1010bca14196bc4bcb86'));
        }

    }

})(current, previous);
