sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDate: function(strDate){
		var currentDate = new GlideDateTime();
		var recDate = new GlideDateTime(strDate);
		//gs.addInfoMessage('current date : '+currentDate.toString());
		//gs.addInfoMessage('When Needed date : '+recDate.toString());
		if(recDate.before(currentDate))
			return false;
		else
			return true;
	},

    type: 'NeedItUtils'
};
