SetParentPriorityIfChildMorethan2.prototype = {
    initialize: function() {},
    fCheckIncidentCountForParent: function(fvParentIncidentNo) {
        var needItRec = new GlideAggregate('x_58872_needit_needit');
        needItRec.addQuery('parent', fvParentIncidentNo);
        needItRec.addAggregate('COUNT');
        needItRec.query();
		if(needItRec.next()){
			var parentIncCount = needItRec.getAggregate('COUNT');
		}
		//gs.addInfoMessage(parentIncCount);
        return parentIncCount;

    },

    type: 'SetParentPriorityIfChildMorethan2'
};
